const express = require("express");
const app = express();
const User = require("../model/User");
const bcrypt = require("bcrypt");
const jwt = require("jsonwebtoken");
const dotenv = require("dotenv").config();
const saltRounds = 10;
const salt = bcrypt.genSaltSync(saltRounds);
const authMiddleware = require("../middleware/authMiddleware");

// app.use(cors());

// Sign Up Route
app.post("/signup", async (req, res) => {
  console.log("SignUP Starting");
  const { username, firstName, lastName, email, password } = req.body;

  const hashedPassword = await bcrypt.hash(password, salt);

  try {
    const existingUser = await User.findOne({ email, username });

    if (existingUser) {
      console.log("Cannot register same user twice!");
      return res.status(409).json({ error: "User Exists" });
    }

    await User.create({
      username,
      firstName,
      lastName,
      email,
      password: hashedPassword,
    });
    res.json("User Created");
    console.log("Signup Successful");
  } catch (err) {
    console.log("Error creating user");

    console.error(err.message);
    res.status(500).json(err);
  }
});

function authenticateToken(req, res, next) {
  const authHeader = req.headers["authorization"];
  const token = authHeader && authHeader.split(" ")[1];

  if (token == null) return res.sendStatus(400);

  jwt.verify(token, process.env.JWT_SECRET, (err, user) => {
    if (err) return res.sendStatus(403);
    req.user = user;
    console.log(user);
    next();
  });
}

// login route
app.post("/login", async (req, res) => {
  const { email, password } = req.body;
  // Find the user by their email
  const user = await User.findOne({ email });

  if (!user) {
    return res.status(401).json({ error: "Email or password is incorrect" });
  }
  if (bcrypt.compareSync(password, user.password)) {
    const accessToken = jwt.sign({ user }, process.env.JWT_SECRET);
    if (res.statusCode === 201 || 200) {
      console.log("Login Successful");
      return res.json({ status: "ok", token: accessToken });
    } else {
      return res.status(500).json({ error: "Error Loggin in" });
    }
  }

  res.status(401).json({ error: "Invalid Password or Credentials" });
});

// Get all users

app.get("/userlists", async (req, res) => {
  console.log("Uh-oh, someone made a req, are they verified");
  const query = req.body.new;

  try {
    const users = query
      ? await User.find().sort({ _id: -1 })
